            FontWizardDlg.FontName = this.htmlEditor1.TextFormatting.FontName;
            FontWizardDlg.FontSize = this.htmlEditor1.TextFormatting.FontSize;
            if (FontWizardDlg.ShowDialog() == DialogResult.OK)
